function $(id){
	return document.getElementById(id);
}
function getToken(access_token){
		if(access_token == ''){
		alert("Vui lòng đọc kỹ hướng dẫn sử dụng!");
		return false;
	} 
	if(access_token == 'https://www.facebook.com/connect/blank.html#_=_'){
		alert("Bạn đã quá chậm ^^! Hãy thao tác nhanh hơn để có thể lấy được token.");
		return false;
	}
	
	if(access_token.match(/access_token=(.*)&expires_in/)){
		token = access_token.match(/access_token=(.*)&expires_in/)[1];
	}
	else{
		token = access_token;
	}
	return token;
}
function getQueryVariable(variable) {
var query = window.location.search.substring(1);
var vars  = query.split("&");
for (var i=0;i<vars.length;i++) {
var pair = vars[i].split("=");
if (pair[0] == variable){
return pair[1];
}
}
return false;
}
function getJSON(url,successHandler,errorHandler){
var ancms = typeof XMLHttpRequest != 'undefined' ? new XMLHttpRequest() : new ActiveXObject('Microsoft.XMLHTTP');
ancms.open('GET',url,true);
ancms.onreadystatechange = function(){
var status,data;
if(ancms.readyState == 4){
status = ancms.status;
if(status == 200){
data = JSON.parse(ancms.responseText);
successHandler && successHandler(data);
}
else{
errorHandler && errorHandler(status);
}
}
};
ancms.send();
}
